/** @jsxImportSource @emotion/react */
import React, { useState, useEffect, useRef } from 'react';
import { css } from '@emotion/react';
import logo from './logo.svg';
import './App.css';

const ongoingList = [
  { title: '开发任务-4', status: '2022-05-22 18:15' },
  { title: '开发任务-6', status: '2022-06-22 18:15' },
  { title: '测试任务-2', status: '2022-07-22 18:15' }
];
const doneList = [
  { title: '开发任务-2', status: '2022-06-24 18:15' },
  { title: '测试任务-1', status: '2022-07-03 18:15' }
];

const kanbanBoardStyles = css`
  flex: 10;
  display: flex;
  flex-direction: row;
  gap: 1rem;
  margin: 0 1rem 1rem;
`;

const kanbanColumnStyles = css`
  flex: 1 1;
  display: flex;
  flex-direction: column;
  border: 1px solid gray;
  border-radius: 1rem;

  & > h2 {
    margin: 0.6rem 1rem;
    padding-bottom: 0.6rem;
    border-bottom: 1px solid gray;

    & > button {
      float: right;
      margin-top: 0.2rem;
      padding: 0.2rem 0.5rem;
      border: 0;
      border-radius: 1rem;
      height: 1.8rem;
      line-height: 1rem;
      font-size: 1rem;
    }
  }

  & > ul {
    flex: 1;
    flex-basis: 0;
    margin: 1rem;
    padding: 0;
    overflow: auto;
  }
`;

export const kanbanCardStyles = css`
  margin-bottom: 1rem;
  padding: 0.6rem 1rem;
  border: 1px solid gray;
  border-radius: 1rem;
  list-style: none;
  background-color: rgba(255, 255, 255, 0.4);
  text-align: left;

  &:hover {
    box-shadow: 0 0.3rem 0.3rem rgba(0, 0, 0, 0.3), inset 0 1px #fff;
  }
`;

export const kanbanCardTitleStyles = css`
  min-height: 3rem;
`;

const MINUTE = 60 * 1000;
const HOUR = 60 * MINUTE;
const DAY = 24 * HOUR;
const UPDATE_INTERVAL = MINUTE;

const KanbanCard = ({ title, status }) => {
  const [displayTime, setDisplayTime] = useState(status)
  useEffect(() => {
    const updateDisplayTime = () => {
      const timePassed = new Date() - new Date(status);
      let relativeTime = '刚刚';
      if(MINUTE <= timePassed && timePassed < HOUR) {
        relativeTime = `${Math.ceil(timePassed / MINUTE)} 分钟前`
      } else if(HOUR <= timePassed && timePassed < DAY) {
        relativeTime = `${Math.ceil(timePassed / HOUR)} 小时前`
      } else if(DAY <= timePassed) {
        relativeTime = `${Math.ceil(timePassed / DAY)} 天前`
      } 
      setDisplayTime(relativeTime)
    }

    const intervalId = setInterval(updateDisplayTime, UPDATE_INTERVAL)
    updateDisplayTime()

    return function cleanUp() {
      clearInterval(intervalId)
    }
  }, [status])

  return (
    <li css={kanbanCardStyles}>
      <div css={kanbanCardTitleStyles}>{title}</div>
      <div css={css`
        text-align: right;
        font-size: 0.8rem;
        color: #333;
      `}>{displayTime}</div>
    </li>
  )
}

const KanbanNewCard = ({ onSubmit }) => {
  const [title, setTitle] = useState('');  
  const inputElem = useRef(null);
  // 保证组件只会在挂载提交是执行一次，组件更新时不在执行
  useEffect(() => {
    inputElem.current.focus()
  }, [])
  const handleChange = (evt) => {
    setTitle(evt.target.value)
  }
  const handleKeyDown = (evt) => {
    if(evt.key === 'Enter') {
      onSubmit(title)
    }
  }
  return (
    <li css={kanbanCardStyles}>
      <h3>添加新卡片</h3>
      <div css={kanbanCardTitleStyles}>
        <input type='text' ref={inputElem} value={title} onChange={handleChange} onKeyDown={handleKeyDown}  />
      </div>
    </li>
  )
}

const KanbanBoard = ({ children }) => (
  <main css={kanbanBoardStyles} >{children}</main>
)

const KanbanColumn = ({children, className, title}) => (
  <section className={className} css={kanbanColumnStyles}>
    <h2>{title}</h2>
    <ul>{children}</ul>
  </section>
)

function App() {
  const [showAdd, setShowAdd] = useState(false);
  const [todoList, setTodoList] = useState([
    { title: '开发任务-1', status: '2022-05-22 18:15' },
    { title: '开发任务-3', status: '2022-06-22 18:15' },
    { title: '开发任务-5', status: '2022-07-22 18:15' },
    { title: '测试任务-3', status: '2022-07-23 18:15' }
  ])

  const handleAdd = (evt) => {
    setShowAdd(true)
  }
  const handleSubmit = (title) => {
    setTodoList(current => [
      {title, status: new Date().toString()},
      ...current
    ])
    setShowAdd(false)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>我的看板</h1>
        <img src={logo} className="App-logo" alt="logo" />
      </header>
      <KanbanBoard>
        <KanbanColumn className='column-todo' title={
          <>    
            待处理<button onClick={handleAdd} disabled={showAdd}>&#8853; 添加新卡片</button>  
          </>
        }>
          {showAdd && <KanbanNewCard onSubmit={handleSubmit} />}
          {
            todoList.map(props => <KanbanCard key={props.title} {...props} />)
          }
        </KanbanColumn>
        <KanbanColumn className='column-ongoing' title="进行中" >
          {
            ongoingList.map(props => <KanbanCard key={props.title} {...props} />)
          }
        </KanbanColumn>
        <KanbanColumn className='column-done' title="已完成">
          <ul>
            {
              doneList.map(props => <KanbanCard key={props.title} {...props} />)  
            }
          </ul>
        </KanbanColumn>
      </KanbanBoard>
    </div>
  );
}

export default App;
